mapper.select:
SELECT SUM(t13.ALL_CARRER_SCORE+t13.JRSDC_CARRER_SCORE+t13.OFFICE_SCORE+t13.PERSONALITY_SCORE+t13.INTERVIEW_SCORE+t13.CERTIFICATE_SCORE+t13.EVALUATE_SCORE+t13.JUDGE_RECOM_SCORE)  
FROM TB_013 t13
WHERE APLCN_DTLS_PROPER_NUM = #{aplcn_dtls_proper_num}

SELECT table2.* FROM( SELECT table1.*, ROWNUM as rnum FROM( SELECT t1.USER_NAME, t1.USER_PHONE, t1.USER_EMAIL, t2.ANNOUNCE_TITLE, t5.APLCN_DTLS_PROPER_NUM, t5.APLCN_DTLS_DATE, t5.APLCN_DTLS_STS, t10.TRIAL_FCLTT_DESCRIPTION, t11.COURT_NAME FROM TB_001 t1, TB_002 t2, TB_005 t5, TB_010 t10, TB_011 t11 WHERE t1.USER_PROPER_NUM = t5.USER_PROPER_NUM AND t5.ANNOUNCE_PROPER_NUM = t2.ANNOUNCE_PROPER_NUM AND t5.TRIAL_FCLTT_PROPER_NUM = t10.TRIAL_FCLTT_PROPER_NUM AND t5.COURT_PROPER_NUM = t11.COURT_PROPER_NUM
AND t5.ANNOUNCE_PROPER_NUM = #{announce_proper_num}
AND t5.TRIAL_FCLTT_PROPER_NUM = #{trial_fcltt_proper_num}
AND t5.COURT_PROPER_NUM = #{court_proper_num}
AND t5.APLCN_DTLS_STS = #{aplcn_dtls_sts}
AND t1.USER_ID LIKE '%' || #{searchWord } || '%'
AND t1.USER_NAME LIKE '%' || #{searchWord } || '%'
AND t1.USER_EMAIL LIKE '%' || #{searchWord } || '%'
AND t1.USER_PHONE LIKE '%' || #{searchWord } || '%'
ORDER BY t5.APLCN_DTLS_PROPER_NUM DESC )table1 )table2
WHERE table2.rnum >= (#{pageNum}-1)*10+1 and table2.rnum <= #{pageNum}*10

SELECT COUNT(*) FROM TB_001 t1, TB_002 t2, TB_005 t5, TB_010 t10, TB_011 t11 WHERE t1.USER_PROPER_NUM = t5.USER_PROPER_NUM AND t5.ANNOUNCE_PROPER_NUM = t2.ANNOUNCE_PROPER_NUM AND t5.TRIAL_FCLTT_PROPER_NUM = t10.TRIAL_FCLTT_PROPER_NUM AND t5.COURT_PROPER_NUM = t11.COURT_PROPER_NUM
AND t5.ANNOUNCE_PROPER_NUM = #{announce_proper_num}
AND t5.TRIAL_FCLTT_PROPER_NUM = #{trial_fcltt_proper_num}
AND t5.COURT_PROPER_NUM = #{court_proper_num}
AND t5.APLCN_DTLS_STS = #{aplcn_dtls_sts}
AND t1.USER_ID LIKE '%' || #{searchWord } || '%'
AND t1.USER_NAME LIKE '%' || #{searchWord } || '%'
AND t1.USER_EMAIL LIKE '%' || #{searchWord } || '%'
AND t1.USER_PHONE LIKE '%' || #{searchWord } || '%'
